import { useState, useEffect } from "react";
const Shorten = ({ longUrl, shortUrl, onAdd, reset, isInvalid }) => {
  const [long, setLong] = useState("");
  const [alias, setAlias] = useState("");
  const [invalid, setInvalid] = useState(false);
  const [counter, setCounter] = useState(0);
  useEffect(() => {
    setInvalid(isInvalid);
  }, [isInvalid]);

  // useEffect(() => {
  //   if (shouldSubmit && long) {
  //     onAdd(long, alias);
  //     setShouldSubmit(false);
  //     setLong("");
  //     setAlias("");
  //   } else if (shouldReset) {
  //     reset();
  //     setShouldReset(false);
  //   }
  //   console.log("shouldSubmit", shouldSubmit);
  //   console.log("shouldReset", shouldReset);
  // }, [shouldSubmit, onAdd, long, alias, reset, shouldReset]);

  // useEffect(() => {

  // })
  const onSubmit = async (e) => {
    e.preventDefault();

    if (!long) {
      alert("Please add a url");
      return;
    } else {
      if (counter % 2 === 0) {
        await onAdd(longUrl, alias);
        if (!invalid) setCounter(counter + 1);
      } else {
        setLong("");
        setAlias("");
        reset();
        setCounter(counter + 1);
      }
    }
    //function to fetch data and change state
  };

  return (
    <div className="container">
      <form className="add-name" onSubmit={onSubmit}>
        <div className="form-control">
          <label>
            {shortUrl ? "Your Long URL" : "Enter a long URL to make a TinyURL"}
          </label>
          <input
            type="text"
            value={longUrl ? longUrl : long}
            onChange={(e) => setLong(e.target.value)}
          />
        </div>
        {shortUrl ? (
          <div className="form-control">
            <label>TinyURL</label>
            <input type="text" value={shortUrl} readOnly="readOnly" />
          </div>
        ) : (
          <div className="form-control">
            <label>Customize your link</label>
            <div>tinyurl.com</div>
            <input
              type="text"
              placeholder="alias"
              value={alias}
              onChange={(e) => setAlias(e.target.value)}
              style={invalid ? { border: "1px red solid" } : {}}
            />
          </div>
        )}
        <input
          type="submit"
          value={shortUrl ? "Make Another" : "Shorten URL"}
          className="btn btn-block"
        ></input>
      </form>
    </div>
  );
};

export default Shorten;
