import { useState, useEffect } from "react";
const Shorten = ({ longUrl, shortUrl, onAdd, reset, isInvalid }) => {
  const [long, setLong] = useState("");
  const [short, setShort] = useState("");
  const [alias, setAlias] = useState("");
  const [invalid, setInvalid] = useState(false);

  useEffect(() => {
    setLong(longUrl);
    setAlias("");
  }, [longUrl]);

  useEffect(() => {
    setShort(shortUrl);
  }, [shortUrl]);

  useEffect(() => {
    setInvalid(isInvalid);
  }, [isInvalid]);

  // useEffect(() => {
  //   setInvalid(isInvalid);
  // }, [isInvalid]);
  // useEffect(() => {
  //   if (shouldSubmit && long) {
  //     onAdd(long, alias);
  //     setShouldSubmit(false);
  //     setLong("");
  //     setAlias("");
  //   } else if (shouldReset) {
  //     reset();
  //     setShouldReset(false);
  //   }
  //   console.log("shouldSubmit", shouldSubmit);
  //   console.log("shouldReset", shouldReset);
  // }, [shouldSubmit, onAdd, long, alias, reset, shouldReset]);

  // useEffect(() => {

  // })
  const onSubmit = async (e) => {
    e.preventDefault();
    if (!long) {
      alert("Please add a url");
      return;
    }
    if (!short) {
      onAdd(long, alias);
    } else {
      reset();
    }
    // if (!long) {
    //   alert("Please add a url");
    //   return;
    // } else {
    //   if (counter % 2 === 0) {
    //     await onAdd(longUrl, alias);
    //     if (!invalid) {
    //       console.log("counter before", counter)
    //       setCounter(counter + 1);
    //       console.log("counter here", counter)
    //     }
    //   } else {
    //     setLong("");
    //     setAlias("");
    //     reset();
    //     setCounter(counter + 1);
    //   }
    // }
  };

  return (
    <div className="container row form-container col-lg-3">
      <form className="add-name container" onSubmit={onSubmit}>
        <div className="form-control border-0">
          <label className="bold">
            {short ? "Your Long URL" : "Enter a long URL to make a TinyURL"}
          </label>
          <input
            type="text"
            value={longUrl ? longUrl : long}
            onChange={(e) => setLong(e.target.value)}
          />
        </div>
        {short ? (
          <div className="form-control border-0 row">
            <label className="bold">TinyURL</label>
            <input type="text" value={shortUrl} readOnly="readOnly" />
          </div>
        ) : (
          <div className="form-control border-0 row">
            <label className="bold">Customize your link</label>
            <div className="col">tinyurl.com</div>
            <input
              type="text"
              placeholder="alias"
              value={alias}
              onChange={(e) => setAlias(e.target.value)}
              style={invalid ? { border: "1px red solid" } : {}}
            />
          </div>
        )}
        <input
          type="submit"
          value={short ? "Make Another" : "Shorten URL"}
          className="btn-submit btn-block-submit"
        ></input>
      </form>
    </div>
  );
};

export default Shorten;
